mapper.update:
UPDATE \${tableName}
set type = #{typeName},
    description  = #{typeDescription},
    last_updated = current timestamp
    where ID = #{id}

mapper.select:
SELECT id, type, description, last_updated
FROM \${type}
WHERE id = #{id}

SELECT c.id, car_number as carNumber, rpt.mark AS ReportingMark, aar.type AS aarType, ty.type AS carType, c.description, c.rfid_tag as rfidTag FROM car c INNER JOIN rpt_mark rpt ON rpt.id = c.rpt_mark LEFT OUTER JOIN aar_type aar ON c.aar_type = aar.id LEFT OUTER JOIN car_type ty ON c.car_type = ty.id
EXISTS ( SELECT * FROM bad_order b WHERE c.id = b.car_id AND in_effect = '1')
c.cplr_type = #{couplerId}
rpt.mark = #{reportingMark}
NOT EXISTS ( SELECT 1 FROM inspection WHERE car_id = c.id AND inspect_date > #{inspectionDate})
ORDER BY reportingMark, car_number
